import { useEffect, useState } from "react";
import BlogList from "./BlogList";
import { Circles } from "react-loader-spinner";
import "./Home.css";

const Home = () => {
  const [todos, setTodos] = useState(null);
  const [isPending, setIsPending] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    setTimeout(() => {
      fetch("http://localhost:8000/todos")
        .then((res) => {
          if (!res.ok) {
            throw Error("Could not fetch data from that resource");
          }
          //console.log(res);
          return res.json();
        })
        .then((data) => {
          setTodos(data);
          setIsPending(false);
          setError = null;
        })
        .catch((err) => {
          setIsPending(false);
          setError(err.message);
        });
    }, 1000);
  }, []);

  return (
    <div className="home">
      {isPending && (
        <div>
          <div>
            <Circles color={"#123abc"} loading />
          </div>
        </div>
      )}
      {error && <div>{error}</div>}
      {todos && <BlogList todos={todos} title="All tags!" />}
    </div>
  );
};

export default Home;
