import { useEffect, useState } from "react";
import BlogList from "./BlogList";
import { InfinitySpin } from "react-loader-spinner";
import "./Home.css";
import "./AddInput.css";

const Home = () => {
  const [todos, setTodos] = useState(null);
  const [isPending, setIsPending] = useState(true);
  const [error, setError] = useState(null);
  const [newToDo, setNewTODo] = useState(null);

  const deleteHandle = (id) => {
    console.log(id);
    const newToDo = todos.filter((todo) => todo.id !== id);
    setTodos(newToDo);
  };

  const addTitle = () => {
    let temp = { id: todos.length + 1, title: newToDo, completed: false };
    if (newToDo.length > 3) {
      setTodos([...todos, temp]);
      // document.getElementById("title").value = null;
      setNewTODo("");
    } else {
      // document.getElementById("title").value = null;
      setNewTODo("");
    }
  };

  const resetInputField = () => {
    setTodos("");
  };

  useEffect(() => {
    setTimeout(() => {
      fetch("http://localhost:8000/todos")
        .then((res) => {
          if (!res.ok) {
            throw Error("Could not fetch data from that resource");
          }
          //console.log(res);
          return res.json();
        })
        .then((data) => {
          setTodos(data);
          setIsPending(false);
          setError = null;
        })
        .catch((err) => {
          setIsPending(false);
          setError(err.message);
        });
    }, 1000);
  }, []);

  return (
    <div className="home">
      {isPending && (
        <div>
          <InfinitySpin width="200" color="#4fa94d" />
        </div>
      )}
      {error === null ? <div></div> : <div>{error}</div>}
      {todos && (
        <BlogList todos={todos} title="All tags!" deleteHandle={deleteHandle} />
      )}
      {todos && (
        <div className="AddInput">
          <input
            type="text"
            placeholder="Type Something"
            id="title"
            value={newToDo}
            onChange={(e) => {
              setNewTODo(e.target.value);
            }}
          />
          <button id="bttn1" onClick={addTitle}>
            Add title
          </button>
          <button id="bttn2" onClick={resetInputField}>
            Reset
          </button>
        </div>
      )}
    </div>
  );
};

export default Home;
