import "./BlogList.css";
const BlogList = ({ todos, title, deleteHandle }) => {
  //or const BlogList = (blogs,title)
  const [newToDo, setNewTODo] = useState(null);
  const addTitle = () => {
    let temp = { id: todos.length + 1, title: newToDo, completed: false };
    if (newToDo.length > 3) {
      setTodos([...todos, temp]);
      // document.getElementById("title").value = null;
      setNewTODo("");
    } else {
      // document.getElementById("title").value = null;
      setNewTODo("");
    }
  };

  const resetInputField = () => {
    setTodos("");
  };

  return (
    <div className="blog-list">
      <h1>{title}</h1>
      {todos.map((todo) => (
        <div className="blog-preview" key={todo.id}>
          <h2>{todo.title}</h2>
          <button onClick={() => deleteHandle(todo.id)}>Delete..</button>
        </div>
      ))}
      <input
        type="text"
        placeholder="Type Something"
        id="title"
        value={newToDo}
        onChange={(e) => {
          setNewTODo(e.target.value);
        }}
      />
      <button onClick={addTitle}>Add title</button>
      <button onClick={resetInputField}>Reset</button>
    </div>
  );
};

export default BlogList;
